function checkpagewidth() {
    if ( $(window).width() > 1000 ) {
        desktop = true;
    } else { desktop = false; }

    $(window).resize(function(){
        if ( $(window).width() > 1000 ) { 
            newdesktop = true;
        } else { newdesktop = false; }
        
        if ( newdesktop != desktop ) {
            location.reload();
        }
    });
}

checkpagewidth();



